// 1) Normaliza payload (às vezes o n8n guarda como string)
let payload = $json;

// Caso 1: veio como string em payload.data (ou em payload.body)
if (typeof payload.data === "string") {
  payload = JSON.parse(payload.data);
} else if (payload.body && typeof payload.body === "string") {
  payload = JSON.parse(payload.body);
} else if (payload.body && typeof payload.body === "object") {
  payload = payload.body;
}

// 2) Pega lista de ofertas
const offers = Array.isArray(payload.data) ? payload.data : [];
const carriers = payload.dictionaries?.carriers || {};

// Helpers
function pickSegmentInfo(itinerary) {
  const segs = itinerary?.segments || [];
  if (!segs.length) return null;

  const first = segs[0];
  const last = segs[segs.length - 1];

  return {
    from: first.departure?.iataCode || null,
    to: last.arrival?.iataCode || null,
    departAt: first.departure?.at || null,
    arriveAt: last.arrival?.at || null,
    duration: itinerary?.duration || null,
    stops: Math.max(0, segs.length - 1),
    segments: segs.map(s => ({
      from: s.departure?.iataCode || null,
      to: s.arrival?.iataCode || null,
      departAt: s.departure?.at || null,
      arriveAt: s.arrival?.at || null,
      carrierCode: s.carrierCode || null,
      number: s.number || null,
      duration: s.duration || null
    }))
  };
}

function airlineNameFromOffer(offer) {
  const code =
    (offer.validatingAirlineCodes && offer.validatingAirlineCodes[0]) ||
    offer.itineraries?.[0]?.segments?.[0]?.carrierCode ||
    null;

  return {
    code,
    name: code ? (carriers[code] || code) : null
  };
}

// 3) Simplifica
const simplified = offers.slice(0, 3).map((offer) => {
  const priceTotal = offer.price?.total || null;
  const currency = offer.price?.currency || null;

  const airline = airlineNameFromOffer(offer);

  const outbound = pickSegmentInfo(offer.itineraries?.[0]);
  const inbound = pickSegmentInfo(offer.itineraries?.[1]);

  return {
    airline: airline.name,
    airlineCode: airline.code,
    price: { total: priceTotal, currency },
    outbound,
    inbound,
    // opcional: guarda o id da oferta (às vezes útil depois)
    offerId: offer.id || null
  };
});

// 4) Saída pequena e limpa
return [{
  json: {
    flightOffers: simplified,
    meta: {
      totalOffersReceived: offers.length
    }
  }
}];
